    private void BTN_ENTRARActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTN_ENTRARActionPerformed
       
        String sql = "from " + UsuarioModel.class.getName() + "where USU_LOGIN like '" + edtUSU_LOGIN.getText() + "' and USU_SENHA like '"+ edtUSU_SENHA.getText()+"'";

        Session session = HibernateUtil.getSessionFactory().openSession();
        Transaction t = session.beginTransaction();
        int lista = session.createQuery(sql).getFirstResult();
        t.commit();
        session.close();
        
        if(lista != 0) {
            JOptionPane.showMessageDialog(rootPane, session, sql, HEIGHT);
        }
    }//GEN-LAST:event_BTN_ENTRARActionPerformed

